INSERT INTO ejercicios (nombre, descripcion, grupo_muscular) VALUES ('Press Banca', 'El press de banca es un ejercicio de peso libre que trabaja la fuerza de empuje del tren superior en el plano sagital. Así mismo, este ejercicio es uno de los 3 que se realizan en la disciplina de powerlifting', 'Pecho');
INSERT INTO ejercicios (nombre, descripcion, grupo_muscular) VALUES ('Sentadillas', 'Las sentadillas son un ejercicio de peso libre que trabaja los músculos de las piernas y glúteos. Es uno de los ejercicios básicos en cualquier rutina de entrenamiento de fuerza.', 'Piernas');
INSERT INTO ejercicios (nombre, descripcion, grupo_muscular) VALUES ('Dominadas', 'Las dominadas son un ejercicio de peso corporal que trabaja principalmente los músculos de la espalda y los brazos. Es excelente para desarrollar fuerza y ​​potencia.', 'Espalda');
INSERT INTO ejercicios (nombre, descripcion, grupo_muscular) VALUES ('Peso Muerto', 'El peso muerto es un ejercicio compuesto que trabaja múltiples grupos musculares, incluyendo la espalda baja, glúteos, isquiotibiales y la cadena posterior en general. Es fundamental para desarrollar fuerza funcional.', 'Espalda');
INSERT INTO ejercicios (nombre, descripcion, grupo_muscular) VALUES ('Flexiones de Brazos', 'Las flexiones de brazos son un ejercicio de peso corporal que trabaja principalmente los músculos del pecho, hombros y tríceps. Son versátiles y se pueden realizar en cualquier lugar sin necesidad de equipo.', 'Pecho');
INSERT INTO ejercicios (nombre, descripcion, grupo_muscular) VALUES ('Zancadas', 'Las zancadas son un ejercicio de piernas que trabajan los músculos de las piernas y glúteos. Pueden realizarse con peso corporal o con pesas adicionales para aumentar la intensidad.', 'Piernas');
INSERT INTO ejercicios (nombre, descripcion, grupo_muscular) VALUES ('Fondos en Paralelas', 'Los fondos en paralelas son un ejercicio de peso corporal que se enfoca en los músculos del tríceps y los hombros. También trabajan los músculos del pecho y el core como músculos secundarios.', 'Tríceps');
INSERT INTO ejercicios (nombre, descripcion, grupo_muscular) VALUES ('Remo con Barra', 'El remo con barra es un ejercicio de espalda que se centra en los músculos de la espalda media y superior, así como en los músculos del brazo. Es un ejercicio compuesto que ayuda a desarrollar fuerza y tamaño muscular.', 'Espalda');
INSERT INTO ejercicios (nombre, descripcion, grupo_muscular) VALUES ('Press de Hombros con Mancuernas', 'El press de hombros con mancuernas es un ejercicio para hombros que se realiza levantando una mancuerna desde los hombros hasta la posición sobre la cabeza. Ayuda a desarrollar los deltoides y la fuerza en los hombros.', 'Hombros');
INSERT INTO ejercicios (nombre, descripcion, grupo_muscular) VALUES ('Elevaciones de Gemelos en Máquina', 'Las elevaciones de gemelos en máquina son un ejercicio para los músculos de la pantorrilla que se realiza levantando los talones mientras se está de pie en una máquina diseñada para ello. Ayudan a desarrollar fuerza y tamaño en los músculos de la pantorrilla.', 'Pantorrillas');
INSERT INTO ejercicios (nombre, descripcion, grupo_muscular) VALUES ('Curl de Bíceps con Barra', 'El curl de bíceps con barra es un ejercicio para bíceps que se realiza levantando una barra desde los muslos hasta los hombros mediante la flexión de los codos. Es uno de los ejercicios básicos para desarrollar fuerza y tamaño en los bíceps.', 'Bíceps');
INSERT INTO ejercicios (nombre, descripcion, grupo_muscular) VALUES ('Flexiones con Manos Elevadas', 'Las flexiones con manos elevadas son una variación de las flexiones de brazos convencionales en las que las manos se colocan en una superficie elevada, como una caja o un banco. Ayudan a aumentar el rango de movimiento y a trabajar más los músculos del pecho.', 'Pecho');
INSERT INTO ejercicios (nombre, descripcion, grupo_muscular) VALUES ('Deadlift Rumano', 'El deadlift rumano es una variante del peso muerto en la que las piernas permanecen casi completamente rectas durante todo el movimiento. Se enfoca en trabajar los músculos de la espalda baja, los glúteos y los isquiotibiales.', 'Espalda');
INSERT INTO ejercicios (nombre, descripcion, grupo_muscular) VALUES ('Press de Banca Inclinado', 'El press de banca inclinado es una variación del press de banca convencional que se realiza en un banco inclinado. Trabaja principalmente los músculos del pecho y los hombros.', 'Pecho');
INSERT INTO ejercicios (nombre, descripcion, grupo_muscular) VALUES ('Hip Thrust', 'El hip thrust es un ejercicio para glúteos que se realiza apoyando la espalda en un banco y levantando las caderas hasta que estén en línea con el cuerpo. Es excelente para desarrollar fuerza y tamaño en los glúteos.', 'Glúteos');
INSERT INTO ejercicios (nombre, descripcion, grupo_muscular) VALUES ('Pull-ups', 'Los pull-ups son un ejercicio de peso corporal que se realiza colgándose de una barra y levantando el cuerpo hasta que la barbilla esté por encima de la barra. Trabajan principalmente los músculos de la espalda y los brazos.', 'Espalda');
INSERT INTO ejercicios (nombre, descripcion, grupo_muscular) VALUES ('Press Militar', 'El press militar es un ejercicio para hombros que se realiza levantando una barra desde los hombros hasta la posición sobre la cabeza. Es excelente para desarrollar fuerza y tamaño en los deltoides.', 'Hombros');
INSERT INTO ejercicios (nombre, descripcion, grupo_muscular) VALUES ('Elevaciones Laterales con Mancuernas', 'Las elevaciones laterales con mancuernas son un ejercicio para los hombros que se realiza levantando las mancuernas hacia los lados hasta que los brazos estén paralelos al suelo. Ayudan a desarrollar los deltoides laterales.', 'Hombros');
INSERT INTO ejercicios (nombre, descripcion, grupo_muscular) VALUES ('Estocadas con Mancuernas', 'Las estocadas con mancuernas son un ejercicio para piernas que se realiza sosteniendo una mancuerna en cada mano mientras se hace el movimiento de estocada. Trabajan los músculos de las piernas y glúteos.', 'Piernas');
INSERT INTO ejercicios (nombre, descripcion, grupo_muscular) VALUES ('Fondos en Anillas', 'Los fondos en anillas son un ejercicio de peso corporal que se realiza utilizando anillas suspendidas. Trabajan principalmente los músculos del pecho, tríceps y hombros, así como también los músculos estabilizadores del core.', 'Pecho');
INSERT INTO ejercicios (nombre, descripcion, grupo_muscular) VALUES ('Sentadillas Sumo', 'Las sentadillas sumo son una variación de las sentadillas convencionales en las que se coloca los pies más anchos que la anchura de los hombros y las puntas de los pies ligeramente hacia afuera. Se enfocan en trabajar los músculos internos del muslo y los glúteos.', 'Piernas');
INSERT INTO ejercicios (nombre, descripcion, grupo_muscular) VALUES ('Flexiones de Brazos con una Sola Mano', 'Las flexiones de brazos con una sola mano son un ejercicio avanzado que implica realizar flexiones con el peso del cuerpo apoyado en una sola mano. Es excelente para desarrollar fuerza y estabilidad en los brazos y hombros.', 'Pecho');
INSERT INTO ejercicios (nombre, descripcion, grupo_muscular) VALUES ('Abdominales con Piernas Elevadas', 'Los abdominales con piernas elevadas son un ejercicio de abdominales que se realiza acostado boca arriba con las piernas elevadas en el aire. Ayuda a trabajar los músculos abdominales inferiores.', 'Abdominales');
INSERT INTO ejercicios (nombre, descripcion, grupo_muscular) VALUES ('Extensiones de Tríceps con Polea Alta', 'Las extensiones de tríceps con polea alta son un ejercicio para el tríceps que se realiza utilizando una máquina de polea alta. Ayuda a aislar y desarrollar los músculos del tríceps.', 'Tríceps');
INSERT INTO ejercicios (nombre, descripcion, grupo_muscular) VALUES ('Flexiones de Brazos Diamante', 'Las flexiones de brazos diamante son una variante de las flexiones de brazos convencionales que se enfocan más en los tríceps. Coloca las manos formando un diamante debajo del pecho mientras haces el movimiento.', 'Tríceps');
INSERT INTO ejercicios (nombre, descripcion, grupo_muscular) VALUES ('Plancha', 'La plancha es un ejercicio de resistencia que fortalece los músculos abdominales, lumbares y del core. Ayuda a mejorar la estabilidad y la postura.', 'Abdominales');
INSERT INTO ejercicios (nombre, descripcion, grupo_muscular) VALUES ('Burpees', 'Los burpees son un ejercicio compuesto que involucra movimientos de flexión, sentadilla, salto y flexiones de brazos. Son excelentes para el entrenamiento de fuerza y cardio.', 'Cuerpo Completo');
